client/src/components/project-ticker.tsx.content:
import { useEffect, useRef } from "react";

const projects = [
  { name: "HydroWatch: IoT Water Management System", link: "#projects" },
  { name: "LeetLingo – AI Interview Prep", link: "#projects" },
  { name: "Polyp Detection using CNN", link: "#projects" },
  { name: "Real-Time Currency Converter", link: "#projects" },
];

export function ProjectTicker() {
  const tickerRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const prefersReducedMotion = window.matchMedia("(prefers-reduced-motion: reduce)").matches;

    if (prefersReducedMotion && tickerRef.current) {
      tickerRef.current.style.animationPlayState = "paused";
    }
  }, []);

  return (
    <div className="relative overflow-hidden bg-gradient-to-r from-slate-900 via-slate-800 to-slate-900 dark:from-black dark:via-slate-950 dark:to-black py-8 border-y border-primary/20">
      <div className="absolute inset-0 bg-gradient-to-r from-primary/5 via-transparent to-primary/5 pointer-events-none"></div>

      <div className="relative">
        <div className="ticker-wrapper">
          <div ref={tickerRef} className="ticker-content" data-testid="project-ticker">
            {/* First set of projects */}
            {projects.map((project, index) => (
              <a
                key={`first-${index}`}
                href={project.link}
                className="ticker-item group"
                data-testid={`ticker-item-${index}`}
              >
                <span className="ticker-text">{project.name}</span>
                <span className="ticker-separator">•</span>
              </a>
            ))}
            {/* Duplicate set for seamless loop */}
            {projects.map((project, index) => (
              <a
                key={`second-${index}`}
                href={project.link}
                className="ticker-item group"
              >
                <span className="ticker-text">{project.name}</span>
                <span className="ticker-separator">•</span>
              </a>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/contact-section.ts.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Mail, Phone, Linkedin, Github, Send } from "lucide-react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";

const contactSchema = z.object({
  name: z.string().min(2, "Name must be at least 2 characters"),
  email: z.string().email("Please enter a valid email address"),
  subject: z.string().min(5, "Subject must be at least 5 characters"),
  message: z.string().min(10, "Message must be at least 10 characters"),
});

type ContactForm = z.infer<typeof contactSchema>;

const contactInfo = [
  {
    icon: Mail,
    label: "Email",
    value: "yrajpoot648@gmail.com",
    href: "mailto:yrajpoot648@gmail.com",
  },
  {
    icon: Phone,
    label: "Phone",
    value: "+91 7876403194",
    href: "tel:+917876403194",
  },
  {
    icon: Linkedin,
    label: "LinkedIn",
    value: "linkedin.com/in/yash-rajput",
    href: "https://linkedin.com/in/yash-rajput",
  },
  {
    icon: Github,
    label: "GitHub",
    value: "github.com/yashrajput4235",
    href: "https://github.com/yashrajput4235",
  },
];

export function ContactSection() {
  const { toast } = useToast();
  const [isSubmitting, setIsSubmitting] = useState(false);

  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
  } = useForm<ContactForm>({
    resolver: zodResolver(contactSchema),
  });

  const contactMutation = useMutation({
    mutationFn: async (data: ContactForm) => {
      const response = await apiRequest("POST", "/api/contact", data);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Message sent successfully!",
        description: "Thank you for reaching out. I'll get back to you soon.",
      });
      reset();
    },
    onError: (error: any) => {
      toast({
        title: "Failed to send message",
        description: error.message || "Please try again later.",
        variant: "destructive",
      });
    },
    onSettled: () => {
      setIsSubmitting(false);
    },
  });

  const onSubmit = (data: ContactForm) => {
    setIsSubmitting(true);
    contactMutation.mutate(data);
  };

  return (
    <section id="contact" className="py-20 bg-muted/30 relative overflow-hidden">
      <div className="absolute inset-0 bg-gradient-to-br from-primary/5 via-transparent to-purple-600/5"></div>
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 relative z-10">
        <div className="text-center mb-16">
          <h2 className="text-3xl md:text-4xl font-bold mb-4">Let's Work Together</h2>
          <p className="text-muted-foreground max-w-2xl mx-auto">
            I'm always open to discussing new opportunities, collaborations, and exciting projects. Feel free to reach out!
          </p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
          {/* Contact Information */}
          <div className="animate-in fade-in slide-in-from-left duration-700">
            <h3 className="text-2xl font-bold mb-6 smooth-appear">Get In Touch</h3>
            <div className="space-y-6 stagger-animation">
              {contactInfo.map((info, index) => (
                <div 
                  key={index} 
                  className="flex items-center group cursor-pointer hover:translate-x-2 transition-all duration-300"
                  data-testid={`contact-info-${index}`}
                  style={{ animationDelay: `${index * 0.1}s` }}
                >
                  <div className="bg-primary/10 p-3 rounded-lg mr-4 group-hover:bg-primary/20 group-hover:scale-110 transition-all duration-300">
                    <info.icon className="w-5 h-5 text-primary group-hover:rotate-12 transition-transform duration-300" />
                  </div>
                  <div>
                    <p className="font-medium">{info.label}</p>
                    <a
                      href={info.href}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-muted-foreground hover:text-primary transition-colors"
                      data-testid={`link-${info.label.toLowerCase()}`}
                    >
                      {info.value}
                    </a>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Contact Form */}
          <div className="animate-in fade-in slide-in-from-right duration-700">
            <div className="glass-effect p-8 rounded-2xl">
              <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
              <div>
                <Label htmlFor="name">Full Name</Label>
                <Input
                  id="name"
                  placeholder="Your full name"
                  {...register("name")}
                  className="mt-1"
                />
                {errors.name && (
                  <p className="text-sm text-destructive mt-1">{errors.name.message}</p>
                )}
              </div>

              <div>
                <Label htmlFor="email">Email Address</Label>
                <Input
                  id="email"
                  type="email"
                  placeholder="your.email@example.com"
                  {...register("email")}
                  className="mt-1"
                />
                {errors.email && (
                  <p className="text-sm text-destructive mt-1">{errors.email.message}</p>
                )}
              </div>

              <div>
                <Label htmlFor="subject">Subject</Label>
                <Input
                  id="subject"
                  placeholder="What's this about?"
                  {...register("subject")}
                  className="mt-1"
                />
                {errors.subject && (
                  <p className="text-sm text-destructive mt-1">{errors.subject.message}</p>
                )}
              </div>

              <div>
                <Label htmlFor="message">Message</Label>
                <Textarea
                  id="message"
                  placeholder="Tell me about your project or opportunity..."
                  rows={5}
                  {...register("message")}
                  className="mt-1 resize-none"
                />
                {errors.message && (
                  <p className="text-sm text-destructive mt-1">{errors.message.message}</p>
                )}
              </div>

              <Button
                type="submit"
                size="lg"
                disabled={isSubmitting}
                className="w-full pulse-glow hover:scale-105 transition-transform duration-300"
              >
                <Send className="w-4 h-4 mr-2" />
                {isSubmitting ? "Sending..." : "Send Message"}
              </Button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

